import { PRICE_FILTER } from '../actions';

export const  priceFilterReducer = (state = {}, action) => {
    switch (action.type) {
        case PRICE_FILTER:
            console.log("action.payload in price filter", action.payload);
            return {...state, 
                min:action.payload.min,
                max:action.payload.max};
         default:
         return state;
    }
};
